     constructHtml(options) {
       const {
         imageWidth,
         imageX,
         imageY,
         name,
         imgPath,
         imgName,
         imgNameWithPattern,
       } = options;
       const htmlEle = `<div class="card lozad" style="width:${imageWidth}px">
