           imgName = imgNameWithPattern.split(".")[0];
           imageSize = item.split(" ")[0];
           imageX = imageSize.split(".")[0];
           imageY = imageSize.split(".")[1];
           let imgOptions = {
             imageWidth,
             imageX,
             imageY,
             name,
             imgName,
             imgPath,
